package lessons.review;

public class MathOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// variable
		
		int myInt1, myInt2, intAnswer1, intAnswer2, intAnswer3, intAnswer4, intAnswer5;
		double myDouble1, myDouble2, doubleAnswer1, doubleAnswer2, doubleAnswer3;
		
		// initialization
		
		myInt1 = 5;
		myInt2 = 3;
		myDouble1 = 5.3;
		myDouble2 = 3.6;
		
		// add
		
		intAnswer1 = myInt1 + myInt2;
		
		// subtract
		
		intAnswer2 = myInt1 - myInt2;
		
		// Multiply
		
		intAnswer3 = myInt1 * myInt2;
		
		// Divide
		
		intAnswer4 = myInt1 / myInt2;
		doubleAnswer1 = myInt1 / myInt2;
		
		// modulus
		
		intAnswer5 = 14 % 3;

		
		//Output
		
		System.out.println(intAnswer1);
		System.out.println(intAnswer2);
		System.out.println(intAnswer3);
		System.out.println(intAnswer4);
		System.out.println(doubleAnswer1);
		System.out.println(intAnswer5);

		
	}

}
